namespace Itoris\Producttabsslider\Model;
use Magento\Framework\DataObject\IdentityInterface;

class ProductTabsValueText extends \Magento\Catalog\Model\AbstractModel implements IdentityInterface
{
    /**
     * Constants for keys of data array. Identical to the name of the getter in snake case
     */
    const VALUE_ID = 'value_id';
    const TAB_ID= 'tab_id';
    const STORE_ID = 'store_id';
    const ATTRIBUTE_ID = 'attribute_id';
    const PRODUCT_ID = 'product_id';
    const VALUE = 'value';
    /**
     * CMS page cache tag
     */
    const CACHE_TAG = 'itoris_product_tabs_value_text';
    /**
     * @var string
     */
    protected $_cacheTag = 'itoris_product_tabs_value_text';

    /**
     * Prefix of model events names
     *
     * @var string
     */
    protected $_eventPrefix = 'itoris_product_tabs_value_text';

    /**
     * Initialize resource model
     *
     * @return void
     */
    protected function _construct()
    {
        $this->_init('Itoris\Producttabsslider\Model\ResourceModel\ProductTabsValueText');
        parent::_construct(); // TODO: Change the autogenerated stub
    }

    /**
     * Return unique ID(s) for each object in system
     *
     * @return array
     */
    public function getIdentities()
    {
        return [self::CACHE_TAG . '_' . $this->getId()];
    }
    /**
     * Get ID
     *
     * @return int|null
     */
    public function getId()
    {
        return $this->getData(self::VALUE_ID);
    }

    public function setId($id)
    {
        return $this->setData(self::VALUE_ID, $id);
    }

    public function getTabId()
    {
        return $this->getData(self::TAB_ID);
    }

    public function setTabId($tabId)
    {
        return $this->setData(self::TAB_ID, $tabId);
    }
    public function getAttributeId()
    {
        return $this->getData(self::ATTRIBUTE_ID);
    }

    public function setAttributeId($attributeId)
    {
        return $this->setData(self::ATTRIBUTE_ID, $attributeId);
    }
    public function getStoreId()
    {
        return $this->getData(self::STORE_ID);
    }

    public function setStoreId($storeId)
    {
        return $this->setData(self::STORE_ID, $storeId);
    }
    public function getProductId()
    {
        return $this->getData(self::PRODUCT_ID);
    }

    public function setProductId($productId)
    {
        return $this->setData(self::PRODUCT_ID, $productId);
    }
    public function getValue()
    {
        return $this->getData(self::VALUE);
    }

    public function setValue($value)
    {
        return $this->setData(self::VALUE, $value);
    }
}